            array('id' => '389','field' => 'seo_name','language' => 'ru','value' => 'Средство для стирки Цветного белья «МИЛАМ CHEMICAL» 4L','localizable_type' => 'Seo','localizable_id' => '40','created_at' => '2022-05-17 11:17:47','updated_at' => '2022-08-20 17:57:36'),
