            array('id' => '807','field' => 'storage_conditions','language' => 'ru','value' => 'от -20°С до + 30 ° С, и относительной влажности воздуха 80%. Избегать попадания влаги и прямых солнечных лучей.','localizable_type' => 'Products','localizable_id' => '24','created_at' => '2022-06-03 11:00:33','updated_at' => '2022-06-03 11:00:33'),
            array('id' => '808','field' => 'storage_conditions','language' => 'ua','value' => 'від -20°С до +30°С, та відносної вологості повітря 80%. Уникати потрапляння вологи та прямих сонячних променів.','localizable_type' => 'Products','localizable_id' => '24','created_at' => '2022-06-03 11:00:33','updated_at' => '2022-06-03 11:00:33'),
