            array('id' => '827','field' => 'seo_name','language' => 'ru','value' => 'Спрей для хромированных и акриловых поверхностей «МИЛАМ» 520ml','localizable_type' => 'Seo','localizable_id' => '67','created_at' => '2022-06-08 09:47:08','updated_at' => '2022-08-18 10:38:45'),
