            array('id' => '1153','field' => 'seo_name','language' => 'ua','value' => 'Засіб для чищення з дезінфікуючим ефектом «МИЛАМ» 500g','localizable_type' => 'Seo','localizable_id' => '84','created_at' => '2022-06-12 09:21:18','updated_at' => '2022-08-20 12:26:53'),
