            array('id' => '1311','field' => 'seo_name','language' => 'ru','value' => 'УНИВЕРСАЛЬЕЫЙ СПРЕЙ «МИЛАМ CHEMICAL»','localizable_type' => 'Seo','localizable_id' => '93','created_at' => '2022-06-13 23:05:00','updated_at' => '2022-06-13 23:05:00'),
