            array('id' => '1344','field' => 'seo_name','language' => 'ru','value' => 'Средство по уходу за Ламинатом «МИЛАМ CHEMICAL» 1L','localizable_type' => 'Seo','localizable_id' => '95','created_at' => '2022-06-13 23:17:58','updated_at' => '2022-08-20 16:17:41'),
