            array('id' => '2221','field' => 'compound','language' => 'en','value' => '<p>Water, hydrogen peroxide (&lt;5%), nonionic surfactants (&lt;5%), anionic surfactants (&lt;5%), sodium tripolyphosphate (&lt;5%), trilon B (&lt;5%), phosphoric acid, perfume, color.</p>','localizable_type' => 'Products','localizable_id' => '12','created_at' => '2022-08-15 16:49:15','updated_at' => '2022-08-20 17:11:29'),
