            array('id' => '2533','field' => 'seo_name','language' => 'en','value' => 'Anti-mold "MILAM CHEMICAL" 750ml','localizable_type' => 'Seo','localizable_id' => '81','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
            array('id' => '2534','field' => 'meta_title','language' => 'en','value' => '','localizable_type' => 'Seo','localizable_id' => '81','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-21 11:49:24'),
            array('id' => '2535','field' => 'name','language' => 'en','value' => 'Anti-mold "MILAM CHEMICAL" 750ml','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
            array('id' => '2536','field' => 'description','language' => 'en','value' => '<p>Effectively removes mold and soap residue from bathtubs, sinks, toilet bowls, tiles, ceramic tiles, plastic curtains, etc. It has whitening power and antibacterial properties. Suitable for sanitary ware, ceramic tiles, plastic surfaces.</p>','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
            array('id' => '2537','field' => 'instructions','language' => 'en','value' => '<p>Use the product only in a ventilated area. Check fuse. Apply the product to the contaminated surface from a distance of 20-30 cm. Wait 10-15 minutes, rub with a damp sponge or brush, repeat the procedure if necessary. Wash off with plenty of water. Before use, test the resistance of the coating to the agent on an inconspicuous area of the surface. When processing metal surfaces, the product should be washed off immediately to prevent rust.</p>','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
            array('id' => '2538','field' => 'security','language' => 'en','value' => '<p><strong>Caution, contains sodium hypochlorite!</strong> Use for its intended purpose, not for internal use! Work with rubber gloves! Do not point sprayer at eyes! If you feel the effects of the vapors of the product, leave the room. Ventilate the room. Use the product only in a ventilated area. In case of contact with skin or eyes, rinse thoroughly with plenty of running water. Seek medical attention if necessary. Do not use for cleaning wooden and aluminum surfaces, clothes, curtains, furniture, silicone insulating materials. Do not mix with other cleaners that contain acids or hydrogen peroxide. Store in an upright position in a dark, cool place out of the reach of children. Joint storage with acid products is not recommended. Afraid of heat. After the expiration date, double the amount of use.</p>','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
            array('id' => '2539','field' => 'compound','language' => 'en','value' => '<p>Water, sodium hypochlorite (&gt; 30%), nonionic surfactants &lt;5%, anionic surfactants &lt;5%, Trilon B, perfume.</p>','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-20 16:27:08'),
            array('id' => '2540','field' => 'shelf_life','language' => 'en','value' => '12 months','localizable_type' => 'Products','localizable_id' => '40','created_at' => '2022-08-18 10:59:26','updated_at' => '2022-08-18 10:59:26'),
