            array('id' => '2870','field' => 'name','language' => 'en','value' => 'Gel for cleaning stoves "MILAM" 500ml','localizable_type' => 'Products','localizable_id' => '73','created_at' => '2022-08-20 09:17:38','updated_at' => '2022-08-20 09:17:38'),
            array('id' => '2871','field' => 'description','language' => 'en','value' => '<p>Means for removing fat, soot, from the surfaces of stoves, ovens, enameled sinks, pots and tiled surfaces.</p>','localizable_type' => 'Products','localizable_id' => '73','created_at' => '2022-08-20 09:17:38','updated_at' => '2022-08-22 08:22:01'),
            array('id' => '2872','field' => 'instructions','language' => 'en','value' => '<p>Shake the contents of the bottle vigorously before use. Apply the gel to the surface for 5-10 minutes. Rub heavily soiled areas with a sponge. Wash off with water.</p>','localizable_type' => 'Products','localizable_id' => '73','created_at' => '2022-08-20 09:17:38','updated_at' => '2022-08-20 09:17:38'),
            array('id' => '2873','field' => 'security','language' => 'en','value' => '<p><strong>Carefully! Sodium hydroxide! Work with rubber gloves! Dangerous for the eyes!</strong> Use as directed. In case of contact with skin or eyes, rinse thoroughly with water. Seek medical attention if necessary. Keep tightly closed and out of the reach of children. Do not mix with other products! .Do not freeze! When cleaning surfaces with an unknown coating, test the product on an inconspicuous area. Do not use on aluminum cookware. Afraid of heat. Do not pour into another container.</p>','localizable_type' => 'Products','localizable_id' => '73','created_at' => '2022-08-20 09:17:38','updated_at' => '2022-08-23 12:35:52'),
            array('id' => '2874','field' => 'compound','language' => 'en','value' => '<p>Water, caustic soda 15-30%, nonionic surfactants &lt;5%, anionic surfactants &lt;5%.</p>','localizable_type' => 'Products','localizable_id' => '73','created_at' => '2022-08-20 09:17:38','updated_at' => '2022-08-20 09:17:38'),
